import React, { FunctionComponent } from 'react';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import PokemonList from './pages/pokemon-list';
import PokemonsDetail from './pages/pokemon-detail';
import PageNotFound from './pages/page-not-found';
import PokemonEdit from './pages/pokemon-edit';
  
const App: FunctionComponent = () => {

 return (
     <Router>
         <div>
             {/* la barre de navigation commun a toutes les pages */}
             <nav>
                 <div className="nav-wrapper teal">
                     {/* <Link >  </Link/> */}
                     <Link to="/" className='brand-logo center' > Pokedex </Link>
                 </div>
             </nav>
             {/* le systeme de gestion des routes de notre application */}
             <Switch>
                 <Route exact path="/" component={PokemonList} />
                 <Route exact path="/pokemons" component={PokemonList} />
                 <Route exact path="/pokemons/:id" component={PokemonsDetail} />
                 <Route exact path="/pokemons/edit/" component={PokemonEdit} />
                 <Route component={PageNotFound} />
             </Switch>
         </div>
     </Router>
 )
}
  
export default App;
